package com.company.threads;import com.company.auxiliaries.Cell;import com.company.auxiliaries.Result;import com.company.operations.Operation;public class ParallelScanThread<T> extends MyThread<T> {    private Result<T> result;    public ParallelScanThread(int minIndex, int maxIndex, T[] sharedGenericArray, int pid,                              Cell<T>[][] sendReceiveTable, int threadsNumber, Operation<T> operation, Result<T> result) {        super(minIndex, maxIndex, sharedGenericArray, pid, sendReceiveTable, threadsNumber, operation);        this.result = result;    }    @Override    public void run() {        doParallelScan(getOperation(), result);    }}